import './tailwind.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useState, useEffect, useCallback } from 'react';
import Navbar from './components/navbar';
import ProductCatalog from './components/productcatalog';
import Cart from './components/cart';
import TrackOrder from './components/trackorder';
import Login from './components/auth/Login';
import Signup from './components/auth/Signup';
import { AuthProvider, useAuth } from './context/AuthContext';
import axios from 'axios';
import Cookies from 'js-cookie';

// Configure axios defaults
axios.defaults.baseURL = 'http://localhost:5000';
axios.defaults.headers.common['Content-Type'] = 'application/json';
axios.defaults.withCredentials = true; // Enable sending cookies

// Add response interceptor for error handling
axios.interceptors.response.use(
  response => response,
  error => {
    if (error.response) {
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      console.error('Error response:', error.response.data);
      if (error.response.status === 401) {
        // Handle unauthorized access
        Cookies.remove('token');
        delete axios.defaults.headers.common['Authorization'];
        window.location.href = '/login';
      }
      return Promise.reject(error.response.data);
    } else if (error.request) {
      // The request was made but no response was received
      console.error('Error request:', error.request);
      return Promise.reject({ error: 'No response from server' });
    } else {
      // Something happened in setting up the request that triggered an Error
      console.error('Error message:', error.message);
      return Promise.reject({ error: error.message });
    }
  }
);

const PrivateRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  if (!user) {
    return <Navigate to="/login" />;
  }

  return children;
};

const PublicRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  if (user) {
    return <Navigate to="/" />;
  }

  return children;
};

const AppContent = () => {
  const { user } = useAuth();
  const [cartItems, setCartItems] = useState([]);
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [shippingAddress, setShippingAddress] = useState('');

  const fetchProducts = useCallback(async () => {
    try {
      setLoading(true);
      setError('');
      const response = await axios.get('/api/products');
      setProducts(response.data);
    } catch (err) {
      console.error('Error fetching products:', err);
      setError(err.error || 'Failed to fetch products');
    } finally {
      setLoading(false);
    }
  }, []);

  const fetchOrders = useCallback(async () => {
    if (!user) return;

    try {
      setLoading(true);
      setError('');
      const response = await axios.get('/api/orders');
      setOrders(response.data);
    } catch (err) {
      console.error('Error fetching orders:', err);
      setError(err.error || 'Failed to fetch orders');
    } finally {
      setLoading(false);
    }
  }, [user]);

  const fetchCartItems = useCallback(async () => {
    if (!user) return;

    try {
      setLoading(true);
      setError('');
      const response = await axios.get('/api/cart');
      setCartItems(response.data);
    } catch (err) {
      console.error('Error fetching cart:', err);
      setError(err.error || 'Failed to fetch cart items');
    } finally {
      setLoading(false);
    }
  }, [user]);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  useEffect(() => {
    if (user) {
      fetchCartItems();
      fetchOrders();
    } else {
      setCartItems([]);
      setOrders([]);
    }
  }, [user, fetchCartItems, fetchOrders]);

  const handleQuantityChange = async (productId, newQuantity) => {
    try {
      setError('');
      await axios.put(
        `/api/cart/${productId}`,
        { quantity: newQuantity }
      );
      await fetchCartItems();
    } catch (err) {
      console.error('Error updating cart quantity:', err);
      throw new Error(err.error || 'Failed to update cart quantity');
    }
  };

  const handleRemoveFromCart = async (productId) => {
    try {
      setError('');
      await axios.delete(`/api/cart/${productId}`);
      await fetchCartItems();
    } catch (err) {
      console.error('Error removing from cart:', err);
      throw new Error(err.error || 'Failed to remove item from cart');
    }
  };

  const handleCheckout = async () => {
    if (!user) {
      setError('Please login to place an order');
      return;
    }

    if (!shippingAddress) {
      setError('Please enter shipping address');
      return;
    }

    if (cartItems.length === 0) {
      setError('Your cart is empty');
      return;
    }

    try {
      const orderItems = cartItems.map(item => ({
        product_id: item.product_id,
        quantity: item.quantity
      }));

      const response = await axios.post('/api/orders', {
        items: orderItems,
        address: shippingAddress
      });

      setCartItems([]);
      setShippingAddress('');
      setError('');
      alert('Order placed successfully!');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to place order');
    }
  };

  const handleAddToCart = async (productId, quantity) => {
    if (!user) {
      throw new Error('Please login to add items to cart');
    }

    try {
      setError('');
      await axios.post(
        '/api/cart',
        { product_id: productId, quantity }
      );
      await fetchCartItems();
    } catch (err) {
      console.error('Error adding to cart:', err);
      throw new Error(err.error || 'Failed to add item to cart');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <main className="container mx-auto px-4 py-8">
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}
        {loading ? (
          <div className="flex justify-center items-center h-64">
            <div className="text-xl">Loading...</div>
          </div>
        ) : (
          <Routes>
            <Route path="/login" element={
              <PublicRoute>
                <Login />
              </PublicRoute>
            } />
            <Route path="/signup" element={
              <PublicRoute>
                <Signup />
              </PublicRoute>
            } />
            <Route path="/" element={
              <PrivateRoute>
                <ProductCatalog 
                  products={products} 
                  onAddToCart={handleAddToCart}
                />
              </PrivateRoute>
            } />
            <Route path="/cart" element={
              <PrivateRoute>
                <Cart 
                  cartItems={cartItems}
                  onQuantityChange={handleQuantityChange}
                  onRemoveItem={handleRemoveFromCart}
                  onCheckout={handleCheckout}
                />
              </PrivateRoute>
            } />
            <Route path="/orders" element={
              <PrivateRoute>
                <TrackOrder orders={orders} />
              </PrivateRoute>
            } />
          </Routes>
        )}
      </main>
      <footer className="bg-green-100 text-center p-4 text-sm text-gray-600">
        © {new Date().getFullYear()} Agrofix. All rights reserved.
      </footer>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <AuthProvider>
        <AppContent />
      </AuthProvider>
    </Router>
  );
};

export default App;
