import './tailwind.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import Navbar from './components/navbar';
import ProductCatalog from './components/productcatalog';
import Cart from './components/cart';
import TrackOrder from './components/trackorder';
import AdminDashboard from './components/admindashboard';
import Login from './components/auth/Login';
import Signup from './components/auth/Signup';

const ProtectedRoute = ({ children, role }) => {
  const user = JSON.parse(localStorage.getItem('user'));
  
  if (!user) {
    console.log('No user found, redirecting to login');
    return <Navigate to="/login" />;
  }

  if (role) {
    console.log('Role required:', role, 'User role:', user.role);
    if (user.role !== role) {
      console.log('Invalid role, redirecting to home');
      return <Navigate to="/" />;
    }
  }

  return children;
};

const AppContent = () => {
  const [cartItems, setCartItems] = useState([]);
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [showOrderPlaced, setShowOrderPlaced] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    console.log('Current user:', user);
    
    // Fetch products from the database
    const fetchProducts = async () => {
      try {
        console.log('Fetching products...');
        const response = await fetch('http://localhost:5000/api/products');
        console.log('Response status:', response.status);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        console.log('Products data:', data);
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    // Fetch orders for the logged-in user
    const fetchOrders = async () => {
      if (user) {
        try {
          console.log('Fetching orders...');
          const response = await fetch('http://localhost:5000/api/orders', {
            headers: {
              'Authorization': `Bearer ${user.token}`
            }
          });
          console.log('Orders response status:', response.status);
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          const data = await response.json();
          console.log('Orders data:', data);
          setOrders(data);
        } catch (error) {
          console.error('Error fetching orders:', error);
        }
      }
    };

    fetchProducts();
    fetchOrders();
  }, []);

  const handleLogin = (userData) => {
    console.log('Login data:', userData);
    localStorage.setItem('user', JSON.stringify(userData));
    window.location.href = userData.role === 'admin' ? '/admin' : '/';
  };

  const handleSignup = (userData) => {
    localStorage.setItem('user', JSON.stringify(userData));
    window.location.href = '/';
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const handleAddToCart = (product) => {
    const existingItem = cartItems.find(item => item.id === product.id);
    if (existingItem) {
      setCartItems(cartItems.map(item => 
        item.id === product.id ? { ...item, quantity: (item.quantity || 0) + 1 } : item
      ));
    } else {
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
    }
  };

  const handleQuantityChange = (index, quantity) => {
    const newCartItems = [...cartItems];
    newCartItems[index].quantity = quantity;
    setCartItems(newCartItems);
  };

  const handlePlaceOrder = async (address) => {
    const user = JSON.parse(localStorage.getItem('user'));
    try {
      const response = await fetch('http://localhost:5000/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify({
          items: cartItems,
          address,
          total_amount: cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0)
        })
      });

      if (response.ok) {
        setShowOrderPlaced(true);
        setCartItems([]);
        setTimeout(() => {
          setShowOrderPlaced(false);
          window.location.href = '/track-order';
        }, 2000);
      }
    } catch (error) {
      console.error('Error placing order:', error);
    }
  };

  const handleStatusChange = async (orderId, newStatus) => {
    const user = JSON.parse(localStorage.getItem('user'));
    try {
      const response = await fetch(`http://localhost:5000/api/orders/${orderId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify({ status: newStatus })
      });

      if (response.ok) {
        setOrders(orders.map(order => 
          order.id === orderId ? { ...order, status: newStatus } : order
        ));
      }
    } catch (error) {
      console.error('Error updating order status:', error);
    }
  };

  const handleAddProduct = async (newProduct) => {
    const user = JSON.parse(localStorage.getItem('user'));
    try {
      const response = await fetch('http://localhost:5000/api/products', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify(newProduct)
      });

      if (response.ok) {
        setProducts([...products, newProduct]);
      }
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  return (
    <div className="App">
      {/* Rest of the component content */}
    </div>
  );
};

export default AppContent;
