import './tailwind.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useState, useEffect, useCallback } from 'react';
import Navbar from './components/navbar';
import ProductCatalog from './components/productcatalog';
import Cart from './components/cart';
import TrackOrder from './components/trackorder';
import Login from './components/auth/Login';
import Signup from './components/auth/Signup';
import { AuthProvider, useAuth } from './context/AuthContext';
import PrivateRoute from './components/PrivateRoute';
import axios from 'axios';
import AdminDashboard from './components/admindashboard';
import * as Sentry from '@sentry/react';
import { BrowserTracing } from '@sentry/tracing';

// Initialize Sentry for error tracking
Sentry.init({
  dsn: process.env.REACT_APP_SENTRY_DSN,
  integrations: [new BrowserTracing()],
  tracesSampleRate: 1.0,
});

// Configure axios defaults
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
axios.defaults.headers.common['Content-Type'] = 'application/json';
axios.defaults.withCredentials = true;

// Add request interceptor for auth token and CSRF
axios.interceptors.request.use(
  config => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }

    // Get CSRF token from cookie
    const csrfToken = document.cookie
      .split('; ')
      .find(row => row.startsWith('XSRF-TOKEN='))
      ?.split('=')[1];

    if (csrfToken) {
      config.headers['X-XSRF-TOKEN'] = csrfToken;
    }

    return config;
  },
  error => {
    return Promise.reject(error);
  }
);

// Add response interceptor for error handling
axios.interceptors.response.use(
  response => response,
  error => {
    if (error.response) {
      const { status, data } = error.response;

      // Log error to Sentry
      Sentry.captureException(error, {
        tags: {
          status,
          endpoint: error.config.url,
          method: error.config.method
        },
        extra: {
          response: data
        }
      });

      switch (status) {
        case 401:
          // Token expired or invalid
          localStorage.removeItem('token');
          window.location.href = '/login';
          break;
        case 403:
          // Forbidden - user doesn't have permission
          console.error('Access forbidden:', data.error);
          break;
        case 404:
          // Resource not found
          console.error('Resource not found:', data.error);
          break;
        case 422:
          // Validation error
          console.error('Validation error:', data.errors);
          break;
        case 429:
          // Too many requests
          console.error('Rate limit exceeded:', data.error);
          break;
        case 500:
          // Server error
          console.error('Server error:', data.error);
          break;
        default:
          console.error('API error:', data.error);
      }

      return Promise.reject({
        message: data.error || 'An error occurred',
        status,
        data: data.errors || data
      });
    } else if (error.request) {
      // The request was made but no response was received
      Sentry.captureException(error, {
        tags: {
          type: 'network_error',
          endpoint: error.config.url,
          method: error.config.method
        }
      });

      console.error('No response received:', error.request);
      return Promise.reject({
        message: 'No response from server. Please check your connection.',
        status: 0
      });
    } else {
      // Something happened in setting up the request that triggered an Error
      Sentry.captureException(error, {
        tags: {
          type: 'request_setup_error',
          endpoint: error.config.url,
          method: error.config.method
        }
      });

      console.error('Request setup error:', error.message);
      return Promise.reject({
        message: 'Error setting up request',
        status: -1
      });
    }
  }
);

const PublicRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  if (user) {
    return <Navigate to="/" />;
  }

  return children;
};

const AppContent = () => {
  const { user } = useAuth();
  const [cartItems, setCartItems] = useState([]);
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [shippingAddress, setShippingAddress] = useState('');

  const fetchProducts = useCallback(async () => {
    try {
      setLoading(true);
      setError('');
      const response = await axios.get('/api/products');
      setProducts(response.data);
    } catch (err) {
      console.error('Error fetching products:', err);
      setError(err.response?.data?.error || 'Failed to fetch products');
    } finally {
      setLoading(false);
    }
  }, []);

  const fetchOrders = useCallback(async () => {
    if (!user) return;

    try {
      setLoading(true);
      setError('');
      const response = await axios.get('/api/orders');
      setOrders(response.data || []);
    } catch (err) {
      console.error('Error fetching orders:', err);
      setError(err.response?.data?.error || 'Failed to fetch orders');
    } finally {
      setLoading(false);
    }
  }, [user]);

  const fetchCartItems = useCallback(async () => {
    if (!user) return;

    try {
      setLoading(true);
      setError('');
      const response = await axios.get('/api/cart');
      setCartItems(response.data || []);
    } catch (err) {
      console.error('Error fetching cart:', err);
      setError(err.response?.data?.error || 'Failed to fetch cart items');
    } finally {
      setLoading(false);
    }
  }, [user]);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  useEffect(() => {
    if (user) {
      fetchCartItems();
      fetchOrders();
    } else {
      setCartItems([]);
      setOrders([]);
    }
  }, [user, fetchCartItems, fetchOrders]);

  const handleQuantityChange = async (productId, newQuantity) => {
    if (!user) {
      setError('Please login to update cart');
      return;
    }

    try {
      setError('');
      await axios.put(
        `/api/cart/${productId}`,
        { quantity: newQuantity }
      );
      // Update local state immediately for better UX
      setCartItems(prevItems => 
        prevItems.map(item => 
          item.id === productId ? { ...item, quantity: newQuantity } : item
        )
      );
      // Then fetch from server to ensure sync
      await fetchCartItems();
    } catch (err) {
      console.error('Error updating cart quantity:', err);
      setError(err.response?.data?.error || 'Failed to update cart quantity');
      // Revert to previous state on error
      await fetchCartItems();
    }
  };

  const handleRemoveFromCart = async (productId) => {
    if (!user) {
      setError('Please login to remove items from cart');
      return;
    }

    try {
      setError('');
      // Update local state immediately for better UX
      setCartItems(prevItems => prevItems.filter(item => item.id !== productId));
      await axios.delete(`/api/cart/${productId}`);
      // Then fetch from server to ensure sync
      await fetchCartItems();
    } catch (err) {
      console.error('Error removing from cart:', err);
      setError(err.response?.data?.error || 'Failed to remove item from cart');
      // Revert to previous state on error
      await fetchCartItems();
    }
  };

  const handleCheckout = async () => {
    if (!user) {
      setError('Please login to place an order');
      return;
    }

    if (!shippingAddress) {
      setError('Please enter shipping address');
      return;
    }

    if (cartItems.length === 0) {
      setError('Your cart is empty');
      return;
    }

    try {
      const response = await axios.post('/api/orders', {
        address: shippingAddress,
        payment_method: 'cashondelivery'
      });

      setCartItems([]);
      setShippingAddress('');
      setError('');
      alert('Order placed successfully!');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to place order');
    }
  };

  const handleAddToCart = async (product, quantity) => {
    if (!user) {
      throw new Error('Please login to add items to cart');
    }

    try {
      setError('');
      // Update local state immediately for better UX
      setCartItems(prevItems => {
        const existingItem = prevItems.find(item => item.product_id === product.id);
        if (existingItem) {
          return prevItems.map(item => 
            item.product_id === product.id 
              ? { ...item, quantity: item.quantity + quantity }
              : item
          );
        }
        return [...prevItems, { ...product, quantity }];
      });

      await axios.post(
        '/api/cart',
        { product_id: product.id, quantity }
      );

      // Then fetch from server to ensure sync
      await fetchCartItems();
    } catch (err) {
      console.error('Error adding to cart:', err.response?.data || err);
      // Revert to previous state on error
      await fetchCartItems();
      throw new Error(err.response?.data?.error || 'Failed to add item to cart');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <main className="container mx-auto px-4 py-8">
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}
        {loading ? (
          <div className="flex justify-center items-center h-64">
            <div className="text-xl">Loading...</div>
          </div>
        ) : (
          <Routes>
            <Route
              path="/"
              element={
                <ProductCatalog 
                  products={products} 
                  onAddToCart={handleAddToCart}
                />
              }
            />
            <Route
              path="/cart"
              element={
                <Cart 
                  cartItems={cartItems}
                  onQuantityChange={handleQuantityChange}
                  onRemoveItem={handleRemoveFromCart}
                  onCheckout={handleCheckout}
                  shippingAddress={shippingAddress}
                  onAddressChange={setShippingAddress}
                />
              }
            />
            <Route
              path="/orders"
              element={
                <TrackOrder orders={orders} />
              }
            />
            <Route
              path="/admin"
              element={
                <PrivateRoute requiredRole="admin">
                  <AdminDashboard />
                </PrivateRoute>
              }
            />
          </Routes>
        )}
      </main>
    </div>
  );
};

const App = () => {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          <Route path="/login" element={
            <PublicRoute>
              <Login />
            </PublicRoute>
          } />
          <Route path="/signup" element={
            <PublicRoute>
              <Signup />
            </PublicRoute>
          } />
          <Route path="/admin/*" element={
            <PrivateRoute requiredRole="admin">
              <AdminDashboard />
            </PrivateRoute>
          } />
          <Route path="/*" element={
            <PrivateRoute>
              <AppContent />
            </PrivateRoute>
          } />
        </Routes>
      </Router>
    </AuthProvider>
  );
};

export default App;
