import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Cart = ({ cartItems, onQuantityChange, onRemoveItem, onCheckout }) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [quantities, setQuantities] = useState({});
  const [address, setAddress] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('cash');

  // Initialize quantities from cart items
  useEffect(() => {
    const initialQuantities = {};
    cartItems.forEach(item => {
      initialQuantities[item.product_id] = item.quantity;
    });
    setQuantities(initialQuantities);
  }, [cartItems]);

  const handleQuantityChange = async (productId, newQuantity) => {
    if (!newQuantity || newQuantity <= 0) {
      setError('Please enter a valid quantity in KG');
      return;
    }

    // Update local state immediately for better UX
    setQuantities(prev => ({
      ...prev,
      [productId]: newQuantity
    }));

    setLoading(true);
    setError('');
    try {
      await onQuantityChange(productId, parseFloat(newQuantity));
    } catch (err) {
      setError(err.message);
      // Revert to previous quantity if update fails
      setQuantities(prev => ({
        ...prev,
        [productId]: cartItems.find(item => item.product_id === productId)?.quantity || 0
      }));
    } finally {
      setLoading(false);
    }
  };

  const handleRemove = async (productId) => {
    try {
      setLoading(true);
      setError('');
      await onRemoveItem(productId);
    } catch (err) {
      console.error('Error removing item:', err);
      setError(err.message || 'Failed to remove item');
    } finally {
      setLoading(false);
    }
  };

  const handleCheckout = async (e) => {
    e.preventDefault();
    if (!address.trim()) {
      setError('Please enter your shipping address');
      return;
    }

    try {
      setLoading(true);
      setError('');
      await onCheckout(address, paymentMethod);
    } catch (err) {
      console.error('Error during checkout:', err);
      setError(err.message || 'Failed to place order');
    } finally {
      setLoading(false);
    }
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => {
      return total + (item.price * item.quantity);
    }, 0);
  };

  if (cartItems.length === 0) {
    return (
      <div className="container mx-auto p-6 text-center">
        <h1 className="text-3xl font-bold mb-6">Your Cart</h1>
        <div className="bg-white rounded-lg shadow-md p-8">
          <img
            src="/empty-cart.png"
            alt="Empty Cart"
            className="w-64 h-64 mx-auto mb-4"
          />
          <p className="text-xl text-gray-600">Your cart is empty</p>
          <p className="text-gray-500">Add some products to your cart to see them here</p>
          <Link to="/" className="mt-4 inline-block text-green-600 hover:text-green-500">
            Continue Shopping
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Your Cart</h1>
      
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div className="divide-y">
          {cartItems.map((item) => (
            <div key={item.id} className="p-4 flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <img
                  src={item.image_url || 'https://via.placeholder.com/100'}
                  alt={item.name}
                  className="w-20 h-20 object-cover rounded"
                />
                <div>
                  <h3 className="font-semibold">{item.name}</h3>
                  <p className="text-gray-600">₹{item.price}/kg</p>
                </div>
              </div>
              <div className="flex items-center space-x-4">
                <div className="flex items-center space-x-2">
                  <button
                    onClick={() => handleQuantityChange(item.product_id, quantities[item.product_id] - 1)}
                    disabled={loading || quantities[item.product_id] <= 1}
                    className="px-2 py-1 border rounded hover:bg-gray-100 disabled:opacity-50"
                  >
                    -
                  </button>
                  <input
                    type="number"
                    value={quantities[item.product_id] || ''}
                    onChange={(e) => handleQuantityChange(item.product_id, e.target.value)}
                    onBlur={(e) => handleQuantityChange(item.product_id, e.target.value)}
                    className="w-24 p-2 border rounded"
                    min="0.1"
                    step="0.1"
                    disabled={loading}
                  />
                  <button
                    onClick={() => handleQuantityChange(item.product_id, quantities[item.product_id] + 1)}
                    disabled={loading}
                    className="px-2 py-1 border rounded hover:bg-gray-100 disabled:opacity-50"
                  >
                    +
                  </button>
                </div>
                <p className="font-semibold">₹{(item.price * item.quantity).toFixed(2)}</p>
                <button
                  onClick={() => handleRemove(item.product_id)}
                  disabled={loading}
                  className="text-red-600 hover:text-red-700 disabled:opacity-50"
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
        </div>

        <div className="p-4 bg-gray-50">
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="address">
              Shipping Address
            </label>
            <textarea
              id="address"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              className="w-full p-2 border rounded"
              rows="3"
              placeholder="Enter your shipping address"
              required
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2">
              Payment Method
            </label>
            <div className="flex space-x-4">
              <label className="flex items-center">
                <input
                  type="radio"
                  value="cash"
                  checked={paymentMethod === 'cash'}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                  className="mr-2"
                />
                Cash on Delivery
              </label>
              <label className="flex items-center">
                <input
                  type="radio"
                  value="card"
                  checked={paymentMethod === 'card'}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                  className="mr-2"
                />
                Card Payment
              </label>
            </div>
          </div>

          <div className="flex justify-between items-center">
            <p className="text-xl font-semibold">Total: ₹{calculateTotal().toFixed(2)}</p>
            <button
              onClick={handleCheckout}
              disabled={loading || !address.trim()}
              className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700 disabled:opacity-50"
            >
              {loading ? 'Processing...' : 'Checkout'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cart;
