import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const Cart = ({ cartItems, onQuantityChange, onRemoveItem, onCheckout }) => {
  const [quantities, setQuantities] = useState({});
  const [shippingAddress, setShippingAddress] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    // Initialize quantities from cart items
    const initialQuantities = {};
    cartItems.forEach(item => {
      initialQuantities[item.product_id] = item.quantity;
    });
    setQuantities(initialQuantities);
  }, [cartItems]);

  const handleQuantityChange = async (productId, newQuantity) => {
    try {
      setQuantities(prev => ({
        ...prev,
        [productId]: newQuantity
      }));
      await onQuantityChange(productId, newQuantity);
      setError('');
    } catch (err) {
      console.error('Error updating quantity:', err);
      setError(err.message || 'Failed to update quantity');
      // Revert to previous quantity
      setQuantities(prev => ({
        ...prev,
        [productId]: cartItems.find(item => item.product_id === productId)?.quantity || 0
      }));
    }
  };

  const handleCheckout = async () => {
    if (!shippingAddress) {
      setError('Please enter shipping address');
      return;
    }

    if (cartItems.length === 0) {
      setError('Your cart is empty');
      return;
    }

    try {
      setLoading(true);
      setError('');
      await onCheckout(shippingAddress, paymentMethod);
    } catch (err) {
      console.error('Error during checkout:', err);
      setError(err.message || 'Failed to place order');
    } finally {
      setLoading(false);
    }
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => {
      return total + (item.price * item.quantity);
    }, 0);
  };

  if (cartItems.length === 0) {
    return (
      <div className="container mx-auto p-4">
        <h2 className="text-2xl font-bold mb-4">Your Cart</h2>
        <p>Your cart is empty</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Your Cart</h1>

      {cartItems.length === 0 ? (
        <div className="text-center py-8">
          <p className="text-gray-600">Your cart is empty</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          <div className="md:col-span-2">
            {cartItems.map(item => (
              <div key={item.id} className="bg-white rounded-lg shadow-md p-6 mb-4">
                <div className="flex items-center justify-between">
                  <div>
                    <h3 className="text-lg font-semibold">{item.name}</h3>
                    <p className="text-gray-600">₹{item.price} per KG</p>
                  </div>
                  <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-2">
                      <button
                        onClick={() => handleQuantityChange(item.product_id, Math.max(0.1, quantities[item.product_id] - 0.1))}
                        className="px-2 py-1 bg-gray-200 rounded"
                      >
                        -
                      </button>
                      <input
                        type="number"
                        value={quantities[item.product_id]}
                        onChange={(e) => handleQuantityChange(item.product_id, parseFloat(e.target.value))}
                        className="w-20 p-1 border rounded text-center"
                        min="0.1"
                        step="0.1"
                      />
                      <button
                        onClick={() => handleQuantityChange(item.product_id, quantities[item.product_id] + 0.1)}
                        className="px-2 py-1 bg-gray-200 rounded"
                      >
                        +
                      </button>
                    </div>
                    <button
                      onClick={() => onRemoveItem(item.product_id)}
                      className="text-red-500 hover:text-red-700"
                    >
                      Remove
                    </button>
                  </div>
                </div>
                <div className="mt-2 text-right">
                  <p className="font-semibold">
                    Total: ₹{(item.price * quantities[item.product_id]).toFixed(2)}
                  </p>
                </div>
              </div>
            ))}
          </div>

          <div className="md:col-span-1">
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-bold mb-4">Order Summary</h2>

              <div className="mb-4">
                <label className="block text-gray-700 mb-2">Shipping Address</label>
                <textarea
                  value={shippingAddress}
                  onChange={(e) => setShippingAddress(e.target.value)}
                  className="w-full p-2 border rounded"
                  rows="3"
                  placeholder="Enter your shipping address"
                  required
                />
              </div>

              <div className="mb-4">
                <label className="block text-gray-700 mb-2">Payment Method</label>
                <select
                  value={paymentMethod}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                  className="w-full p-2 border rounded"
                >
                  <option value="cash">Cash on Delivery</option>
                  <option value="online">Online Payment</option>
                </select>
              </div>

              <div className="border-t pt-4">
                <div className="flex justify-between mb-2">
                  <span>Subtotal</span>
                  <span>₹{calculateTotal().toFixed(2)}</span>
                </div>
                <div className="flex justify-between mb-2">
                  <span>Delivery Charges</span>
                  <span>₹0.00</span>
                </div>
                <div className="flex justify-between font-bold text-lg">
                  <span>Total</span>
                  <span>₹{calculateTotal().toFixed(2)}</span>
                </div>
              </div>

              {error && (
                <div className="mt-4 text-red-500 text-sm">
                  {error}
                </div>
              )}

              <button
                onClick={handleCheckout}
                disabled={loading}
                className={`w-full mt-4 py-2 px-4 rounded ${
                  loading ? 'bg-gray-400' : 'bg-green-500 hover:bg-green-600'
                } text-white`}
              >
                {loading ? 'Processing...' : 'Place Order'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
