import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';

const ProductCatalog = ({ products, onAddToCart }) => {
  const { user } = useAuth();
  const [quantities, setQuantities] = useState({});
  const [error, setError] = useState('');

  const handleQuantityChange = (productId, value) => {
    setQuantities(prev => ({
      ...prev,
      [productId]: value
    }));
  };

  const handleAddToCart = async (productId) => {
    const quantity = quantities[productId];
    if (!quantity || quantity <= 0) {
      setError('Please enter a valid quantity in KG');
      return;
    }
    try {
      await onAddToCart(productId, quantity);
      setQuantities(prev => ({
        ...prev,
        [productId]: ''
      }));
      setError('');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Products</h1>
      
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {products.map((product) => (
          <div key={product.id} className="bg-white rounded-lg shadow-md overflow-hidden">
            <img
              src={product.image_url || 'https://via.placeholder.com/300'}
              alt={product.name}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-2">{product.name}</h2>
              <p className="text-gray-600 mb-2">{product.description}</p>
              <p className="text-lg font-bold mb-2">â‚¹{product.price}/kg</p>
              <p className="text-gray-600 mb-4">Available: {product.stock} kg</p>
              
              {user && (
                <div className="flex items-center space-x-4">
                  <div className="flex-1">
                    <input
                      type="number"
                      placeholder="Quantity in KG"
                      value={quantities[product.id] || ''}
                      onChange={(e) => handleQuantityChange(product.id, e.target.value)}
                      className="w-full p-2 border rounded"
                      min="0.1"
                      step="0.1"
                    />
                  </div>
                  <button
                    onClick={() => handleAddToCart(product.id)}
                    className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                  >
                    Add to Cart
                  </button>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductCatalog;
