import './tailwind.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useState } from 'react';
import Navbar from './components/navbar';
import ProductCatalog from './components/productcatalog';
import Cart from './components/cart';
import TrackOrder from './components/trackorder';
import AdminDashboard from './components/admindashboard';
import Login from './components/auth/Login';
import Signup from './components/auth/Signup';

const ProtectedRoute = ({ children, role }) => {
  const user = JSON.parse(localStorage.getItem('user'));
  if (!user) {
    return <Navigate to="/login" />;
  }
  if (role && user.role !== role) {
    return <Navigate to="/" />;
  }
  return children;
};

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([
    { id: 1, name: "Organic Tomatoes", price: 20, image: "https://source.unsplash.com/featured/?tomatoes", stock: 100 },
    { id: 2, name: "Fresh Carrots", price: 15, image: "https://source.unsplash.com/featured/?carrots", stock: 80 },
    { id: 3, name: "Potatoes", price: 10, image: "https://source.unsplash.com/featured/?potatoes", stock: 120 },
    { id: 4, name: "Broccoli", price: 25, image: "https://source.unsplash.com/featured/?broccoli", stock: 60 }
  ]);

  const handleLogin = (userData) => {
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleSignup = (userData) => {
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const handleAddToCart = (product) => {
    const existingItem = cartItems.find(item => item.id === product.id);
    if (existingItem) {
      setCartItems(cartItems.map(item => 
        item.id === product.id ? { ...item, quantity: (item.quantity || 0) + 1 } : item
      ));
    } else {
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
    }
  };

  const handleQuantityChange = (index, quantity) => {
    const newCartItems = [...cartItems];
    newCartItems[index].quantity = quantity;
    setCartItems(newCartItems);
  };

  const handlePlaceOrder = (address, paymentMethod) => {
    const user = JSON.parse(localStorage.getItem('user'));
    const newOrder = {
      id: Date.now(),
      customerName: user.name,
      items: cartItems,
      address,
      paymentMethod,
      status: 'Pending',
      date: new Date().toISOString()
    };
    setOrders([...orders, newOrder]);
    setCartItems([]);
  };

  const handleStatusChange = (orderId, newStatus) => {
    setOrders(orders.map(order => 
      order.id === orderId ? { ...order, status: newStatus } : order
    ));
  };

  const handleAddProduct = (newProduct) => {
    setProducts([...products, { ...newProduct, id: Date.now() }]);
  };

  const handleUpdateProduct = (productId, updatedProduct) => {
    setProducts(products.map(product => 
      product.id === productId ? { ...product, ...updatedProduct } : product
    ));
  };

  return (
    <Router>
      <div className="min-h-screen flex flex-col">
        <Navbar cartCount={cartItems.length} onLogout={handleLogout} />
        <main className="flex-grow">
          <Routes>
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
            <Route path="/signup" element={<Signup onSignup={handleSignup} />} />
            <Route path="/" element={
              <ProtectedRoute>
                <ProductCatalog products={products} onAddToCart={handleAddToCart} />
              </ProtectedRoute>
            } />
            <Route path="/cart" element={
              <ProtectedRoute>
                <Cart 
                  cartItems={cartItems}
                  onQuantityChange={handleQuantityChange}
                  onPlaceOrder={handlePlaceOrder}
                />
              </ProtectedRoute>
            } />
            <Route path="/track-order" element={
              <ProtectedRoute>
                <TrackOrder orders={orders} />
              </ProtectedRoute>
            } />
            <Route path="/admin" element={
              <ProtectedRoute role="admin">
                <AdminDashboard 
                  orders={orders}
                  products={products}
                  onStatusChange={handleStatusChange}
                  onAddProduct={handleAddProduct}
                  onUpdateProduct={handleUpdateProduct}
                />
              </ProtectedRoute>
            } />
          </Routes>
        </main>
        <footer className="bg-green-100 text-center p-4 text-sm text-gray-600">
          © {new Date().getFullYear()} Agrofix. All rights reserved.
        </footer>
      </div>
    </Router>
  );
};

export default App;
