import './tailwind.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import Navbar from './components/navbar';
import ProductCatalog from './components/productcatalog';
import Cart from './components/cart';
import TrackOrder from './components/trackorder';
import AdminDashboard from './components/admindashboard';
import Login from './components/auth/Login';
import Signup from './components/auth/Signup';
import { AuthProvider, useAuth } from './context/AuthContext';
import axios from 'axios';

// Configure axios defaults
axios.defaults.baseURL = 'http://localhost:5000';
axios.defaults.headers.common['Content-Type'] = 'application/json';

const PrivateRoute = ({ children, requiredRole }) => {
  const { user } = useAuth();
  
  if (!user) {
    return <Navigate to="/login" />;
  }

  if (requiredRole && user.role !== requiredRole) {
    return <Navigate to="/" />;
  }

  return children;
};

const AppContent = () => {
  const { user, token } = useAuth();
  const [cartItems, setCartItems] = useState([]);
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [showOrderPlaced, setShowOrderPlaced] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchProducts = async () => {
    try {
      console.log('Fetching products...');
      const response = await axios.get('/api/products');
      console.log('Response status:', response.status);
      console.log('Products data:', response.data);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchOrders = async () => {
    if (!token) return; // Don't fetch if no token
    
    try {
      console.log('Fetching orders...');
      const response = await axios.get('/api/orders', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      console.log('Orders response status:', response.status);
      setOrders(response.data);
    } catch (error) {
      console.error('Error fetching orders:', error);
      setError('Failed to fetch orders');
    }
  };

  const fetchCartItems = async () => {
    if (!token) return; // Don't fetch if no token
    
    try {
      setLoading(true);
      const response = await axios.get('/api/cart', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setCartItems(response.data);
      setError('');
    } catch (err) {
      console.error('Error fetching cart:', err);
      setError('Failed to fetch cart items');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  useEffect(() => {
    if (token) {
      fetchCartItems();
      fetchOrders();
    } else {
      setCartItems([]); // Clear cart when user logs out
      setOrders([]); // Clear orders when user logs out
    }
  }, [token]);

  const handleLogin = (userData) => {
    console.log('Login data:', userData);
    localStorage.setItem('user', JSON.stringify(userData));
    window.location.href = userData.role === 'admin' ? '/admin' : '/';
  };

  const handleSignup = (userData) => {
    console.log('Signup data:', userData);
    localStorage.setItem('user', JSON.stringify(userData));
    window.location.href = '/';
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setCartItems([]); // Clear cart on logout
    window.location.href = '/login';
  };

  const handleQuantityChange = async (productId, newQuantity) => {
    if (newQuantity < 1) return;
    
    try {
      setLoading(true);
      const response = await axios.put(
        `/api/cart/${productId}`,
        { quantity: newQuantity },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      setCartItems(response.data);
    } catch (err) {
      setError('Failed to update cart');
      console.error('Error updating cart:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleRemoveItem = async (productId) => {
    try {
      setLoading(true);
      const response = await axios.delete(
        `/api/cart/${productId}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      setCartItems(response.data);
    } catch (err) {
      setError('Failed to remove item');
      console.error('Error removing item:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleCheckout = async () => {
    try {
      setLoading(true);
      await axios.post(
        '/api/orders',
        { items: cartItems },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      setCartItems([]);
      setShowOrderPlaced(true);
      setTimeout(() => {
        setShowOrderPlaced(false);
        window.location.href = '/track-order';
      }, 2000);
    } catch (err) {
      setError('Failed to place order');
      console.error('Error placing order:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      const response = await axios.put(
        `/api/orders/${orderId}`,
        { status: newStatus },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      setOrders(orders.map(order => 
        order.id === orderId ? { ...order, status: newStatus } : order
      ));
    } catch (error) {
      console.error('Error updating order status:', error);
    }
  };

  const handleAddToCart = async (productId) => {
    if (!token) return; // Don't add if no token
    
    try {
      setLoading(true);
      await axios.post(
        '/api/cart',
        { productId, quantity: 1 },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      // Refresh cart items after adding
      await fetchCartItems();
      setError('');
    } catch (err) {
      console.error('Error adding to cart:', err);
      setError('Failed to add item to cart');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Router>
      <div className="min-h-screen bg-gray-100">
        <Navbar cartCount={cartItems.length} onLogout={handleLogout} />
        <main className="flex-grow">
          {loading ? (
            <div className="flex justify-center items-center h-full">
              <div className="text-xl">Loading products...</div>
            </div>
          ) : error ? (
            <div className="flex justify-center items-center h-full">
              <div className="text-xl text-red-600">Error: {error}</div>
            </div>
          ) : (
            <Routes>
              <Route path="/login" element={<Login onLogin={handleLogin} />} />
              <Route path="/signup" element={<Signup onSignup={handleSignup} />} />
              <Route path="/" element={
                <PrivateRoute>
                  <ProductCatalog 
                    products={products} 
                    onAddToCart={handleAddToCart}
                  />
                </PrivateRoute>
              } />
              <Route path="/cart" element={
                <PrivateRoute>
                  <Cart 
                    cartItems={cartItems}
                    loading={loading}
                    error={error}
                    onQuantityChange={handleQuantityChange}
                    onRemoveItem={handleRemoveItem}
                    onCheckout={handleCheckout}
                  />
                </PrivateRoute>
              } />
              <Route path="/track-order" element={
                <PrivateRoute>
                  <TrackOrder orders={orders} />
                </PrivateRoute>
              } />
              <Route path="/admin" element={
                <PrivateRoute requiredRole="admin">
                  <AdminDashboard 
                    orders={orders}
                    products={products}
                    onStatusChange={handleStatusChange}
                  />
                </PrivateRoute>
              } />
            </Routes>
          )}
        </main>
        <footer className="bg-green-100 text-center p-4 text-sm text-gray-600">
          © {new Date().getFullYear()} Agrofix. All rights reserved.
        </footer>

        {/* Order Placed Notification */}
        {showOrderPlaced && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
            <div className="bg-white p-8 rounded-lg shadow-xl text-center">
              <h2 className="text-3xl font-bold text-green-600 mb-4">Order Placed!</h2>
              <p className="text-xl">Your order has been successfully placed.</p>
            </div>
          </div>
        )}
      </div>
    </Router>
  );
};

const App = () => {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
};

export default App;
