import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Cart = ({ cartItems, onQuantityChange, onPlaceOrder }) => {
  const [showAddedToCart, setShowAddedToCart] = useState(false);
  const [address, setAddress] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('cash');

  const total = cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);

  const handlePlaceOrder = (e) => {
    e.preventDefault();
    if (!address.trim()) {
      alert('Please enter your shipping address');
      return;
    }
    onPlaceOrder(address, paymentMethod);
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">Shopping Cart</h1>
      {cartItems.length === 0 ? (
        <div className="text-center">
          <p className="text-xl mb-4">Your cart is empty</p>
          <Link to="/" className="text-green-600 hover:text-green-800">
            Continue Shopping
          </Link>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="md:col-span-2">
            {cartItems.map((item, index) => (
              <div key={item.id} className="bg-white p-4 rounded-lg shadow mb-4">
                <div className="flex items-center">
                  <img
                    src={item.image_url}
                    alt={item.name}
                    className="w-20 h-20 object-cover rounded"
                  />
                  <div className="ml-4 flex-grow">
                    <h3 className="text-lg font-semibold">{item.name}</h3>
                    <p className="text-gray-600">₹{item.price}</p>
                    <div className="flex items-center mt-2">
                      <button
                        onClick={() => onQuantityChange(index, Math.max(1, item.quantity - 1))}
                        className="bg-gray-200 px-2 py-1 rounded"
                      >
                        -
                      </button>
                      <span className="mx-2">{item.quantity}</span>
                      <button
                        onClick={() => onQuantityChange(index, item.quantity + 1)}
                        className="bg-gray-200 px-2 py-1 rounded"
                      >
                        +
                      </button>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className="font-semibold">
                      ₹{(item.price * item.quantity).toFixed(2)}
                    </p>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="md:col-span-1">
            <div className="bg-white p-6 rounded-lg shadow">
              <h2 className="text-xl font-semibold mb-4">Order Summary</h2>
              <div className="mb-4">
                <p className="flex justify-between mb-2">
                  <span>Subtotal</span>
                  <span>
                    ₹{cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0).toFixed(2)}
                  </span>
                </p>
                <p className="flex justify-between mb-2">
                  <span>Shipping</span>
                  <span>Free</span>
                </p>
                <p className="flex justify-between font-semibold text-lg">
                  <span>Total</span>
                  <span>
                    ₹{cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0).toFixed(2)}
                  </span>
                </p>
              </div>
              <form onSubmit={handlePlaceOrder}>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Shipping Address</label>
                  <textarea
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    className="w-full p-2 border rounded"
                    rows="3"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Payment Method</label>
                  <select
                    value={paymentMethod}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                    className="w-full px-3 py-2 border rounded"
                  >
                    <option value="cash">Cash on Delivery</option>
                    <option value="card">Card Payment</option>
                    <option value="upi">UPI</option>
                  </select>
                </div>
                <button
                  type="submit"
                  className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700"
                >
                  Place Order
                </button>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Added to Cart Notification */}
      {showAddedToCart && (
        <div className="fixed bottom-4 right-4 bg-green-500 text-white px-6 py-3 rounded-lg shadow-lg">
          Added to Cart!
        </div>
      )}
    </div>
  );
};

export default Cart;
