import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import PlaceholderImage from './PlaceholderImage';

const ProductCatalog = ({ products, onAddToCart }) => {
  const { user } = useAuth();
  const [quantities, setQuantities] = useState({});
  const [error, setError] = useState('');

  const handleQuantityChange = (productId, value) => {
    const quantity = parseInt(value);
    if (quantity >= 0) {
      setQuantities(prev => ({
        ...prev,
        [productId]: quantity
      }));
    }
  };

  const handleAddToCart = async (product) => {
    const quantity = quantities[product.id] || 0;

    if (quantity <= 0) {
      setError('Please enter a valid quantity');
      return;
    }

    if (quantity > product.stock) {
      setError(`Only ${product.stock} items available in stock`);
      return;
    }

    try {
      await onAddToCart(product.id, quantity);
      setQuantities(prev => ({
        ...prev,
        [product.id]: 0
      }));
      setError('');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Products</h1>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {products.map((product) => (
          <div key={product.id} className="bg-white rounded-lg shadow-md overflow-hidden">
            <div className="h-48 bg-gray-200 flex items-center justify-center">
              {product.image_url ? (
                <img
                  src={product.image_url}
                  alt={product.name}
                  className="h-full w-full object-cover"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.parentElement.innerHTML = '';
                    e.target.parentElement.appendChild(
                      <PlaceholderImage className="h-full w-full object-cover" />
                    );
                  }}
                />
              ) : (
                <PlaceholderImage className="h-full w-full object-cover" />
              )}
            </div>
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-2">{product.name}</h2>
              <p className="text-gray-600 mb-2">{product.description}</p>
              <p className="text-lg font-bold mb-2">${product.price.toFixed(2)}</p>
              <p className="text-sm text-gray-500 mb-4">
                Stock: {product.stock}
              </p>

              {user && (
                <div className="flex items-center space-x-4">
                  <input
                    type="number"
                    min="0"
                    max={product.stock}
                    value={quantities[product.id] || 0}
                    onChange={(e) => handleQuantityChange(product.id, e.target.value)}
                    className="w-20 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  />
                  <button
                    onClick={() => handleAddToCart(product)}
                    disabled={!quantities[product.id] || quantities[product.id] <= 0}
                    className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Add to Cart
                  </button>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductCatalog;
